    public home() {
        initComponents();
        cardLayout = (CardLayout) contentPanel.getLayout();
        this.setTitle("Craft Beer");
        this.setLocationRelativeTo(null);
        btnDel.setVisible(false);
        txtTotal.setEnabled(false);

        initTable();
        tbPro.setDefaultEditor(Object.class, null);
        tbSale.setDefaultEditor(Object.class, null);
        boolean file = con.loadFile();
        boolean fileSale = con.loadFileSale();
        if (file) {
            fillToTable();
        } else {
            JOptionPane.showConfirmDialog(null, "Nothing to show");
        }
        if (fileSale) {
            fillToTableSale();
        } else {
            JOptionPane.showMessageDialog(null, "Nothing to show Sale");
        }
        con.loadcbPro();
    }

    private void fillToTable() {
        tbModel.setRowCount(0);
        for (Product c : con.proList) {
            Object[] row = new Object[]{c.getId(), c.getName(), c.getPrice(), c.getNation(), c.getDes()};
            tbModel.addRow(row);
        }
    }

    private void fillToTableSale() {
        tbModelSale.setRowCount(0);
        for (Sale s : con.saleList) {
            Object[] row = new Object[]{s.getId(), s.getName(), s.getSnack(), s.getDate(), s.getTotal()};
            tbModelSale.addRow(row);
        }
    }

    private void initTable() {
        String[] columnNames = {"ID", "Name", "Price", "Nation", "Description"};
        String[] columnName = {"ID", "Name", "Snack", "Date", "Total"};
        tbModel = new DefaultTableModel(columnNames, 0);
        tbModelSale = new DefaultTableModel(columnName, 0);
        tbPro.setModel(tbModel);
        tbSale.setModel(tbModelSale);
    }

    private void filter(String kw) {
        TableRowSorter<DefaultTableModel> tr = new TableRowSorter<>(tbModel);
        tbPro.setRowSorter(tr);

        if (!"Choose types of beer".equals(kw)) {
            tr.setRowFilter(RowFilter.regexFilter(kw, 3));
        } else {
            tbPro.setRowSorter(tr);
        }
    }

    private void checkOp(JCheckBox ck) {
        String value = ck.getActionCommand();
        if (ck.isSelected()) {
            con.snacks.add(value);
            con.total += 10000;
        }
    }

    private String checkInput() {
        return (cbPro.getSelectedIndex() == 0) ? "" : cbPro.getSelectedItem().toString();
    }

