    private void jLabel2MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel2MousePressed
        cardLayout.show(contentPanel, "mgPro");
        cbNation.setSelectedIndex(0);
    }//GEN-LAST:event_jLabel2MousePressed

    private void jLabel4MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel4MousePressed
        cardLayout.show(contentPanel, "mgSale");
        int itemCount = cbPro.getItemCount();
        for (int i = 0; i < itemCount; i++) {
            cbPro.removeItemAt(0);
        }
        con.loadcbPro();
        cbPro.setSelectedIndex(0);
        txtTotal.setText("");
        rdSquid.setSelected(false);
        rdPeanuts.setSelected(false);
        cbPro.setBackground(Color.white);
    }//GEN-LAST:event_jLabel4MousePressed

