    private void btnDelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDelActionPerformed
        int result = JOptionPane.showConfirmDialog(
                this,
                "Do you want delete?", "Delete?",
                JOptionPane.YES_NO_OPTION,
                JOptionPane.WARNING_MESSAGE
        );
        if (result == JOptionPane.YES_OPTION) {
            con.deletePro();
            fillToTable();
            con.saveFilePro();
        }
    }//GEN-LAST:event_btnDelActionPerformed

    private void cbNationItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbNationItemStateChanged
        btnDel.setVisible(false);
        String kw = cbNation.getSelectedItem().toString();
        filter(kw);
    }//GEN-LAST:event_cbNationItemStateChanged

    private void tbSaleMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tbSaleMousePressed
        con.currentRow = tbSale.getSelectedRow();
    }//GEN-LAST:event_tbSaleMousePressed

    private void btnAddSaleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddSaleActionPerformed
        con.total = 0;
        con.checkName = checkInput();
        if (!"".equals(con.checkName)) {
            checkOp(rdSquid);
            checkOp(rdPeanuts);
            con.addSale();
            JOptionPane.showMessageDialog(null, "Add successfully");
            fillToTableSale();
        } else {
            cbPro.setBackground(Color.red);
        }
    }//GEN-LAST:event_btnAddSaleActionPerformed

    private void btnTestActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTestActionPerformed
        con.total = 0;
        con.checkName = checkInput();
        if (!"".equals(con.checkName)) {
            checkOp(rdSquid);
            checkOp(rdPeanuts);
            con.testSale();
            txtTotal.setText(String.valueOf(con.total));
        } else {
            cbPro.setBackground(Color.red);
            txtTotal.setText("");
        }
    }//GEN-LAST:event_btnTestActionPerformed

    private void cbProFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_cbProFocusGained
        cbPro.setBackground(Color.white);
    }//GEN-LAST:event_cbProFocusGained

    private void tbProMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tbProMouseClicked
        if (evt.getClickCount() == 2) {
            String name = (String) tbPro.getValueAt(con.currentRow, 1);
            addForm.txtName.setText(name);
            int price = (int) tbPro.getValueAt(con.currentRow, 2);
            addForm.txtPrice.setText(String.valueOf(price));
            String nation = (String) tbPro.getValueAt(con.currentRow, 3);
            addForm.cbNation.setSelectedItem(nation);
            String des = (String) tbPro.getValueAt(con.currentRow, 4);
            addForm.txtDes.setText(des);

            addForm.upPro.setVisible(true);
            addForm.savePro.setVisible(false);
            addForm.lbUp.setVisible(true);
            addForm.lbAdd.setVisible(false);

            addForm.setVisible(true);
            this.setVisible(false);
        }
    }//GEN-LAST:event_tbProMouseClicked

